SELECT SATISBARKODU TableNumber,H.STOKADI ProductName,B.OLUSTURMATARIHI Date, (H.MIKTAR*H.FIYAT)  AS TUTAR,ISNULL(K.KODU,'')+'-'+ISNULL(K.ADI,'') username FROM TBLSPOSSATISBASLIK B WITH(NOLOCK) 
LEFT JOIN TBLSPOSSATISHAREKET H WITH(NOLOCK) ON H.BASLIKIND=B.IND
LEFT JOIN TBLSPOSKULLANICILAR K WITH(NOLOCK) ON K.IND=B.KULLANICIIND
WHERE B.OLUSTURMATARIHI>='{TARIH1}' AND B.OLUSTURMATARIHI<='{TARIH2}' AND SATISBARKODU='{TableNumber}' 